      idxs: 0, // 点击点的纵坐标
      isClickArr: [{
                data: null,
                idx: null,
                idxs: null,
                isClick: null
      }], // 存放点击过的点的数组
      twoArray: Array(20).fill([]), // 存放点击过的点的数组
